import axios from 'axios';
import React, { useState } from 'react'
import { Navigate } from 'react-router-dom';
import { Button, Input, Label } from 'reactstrap';

const Login = () => {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
  return (
    <div>
        <div>
            <h3>Giriş Yap</h3>
        </div>
        <div style={{display: 'flex', justifyContent: 'center'}}>
            <form style={{width: '25rem'}}>
                <Label htmlFor='uname'>Kullanıcı Adı</Label>
                <Input id='uname' type='text' name='username' onChange={(e) => {
                    e.preventDefault();
                    setUsername(e.target.value);
                }}/>
                <Label htmlFor='pwd'>Şifre</Label>
                <Input id='pwd' type='password' name='password' onChange={(e) => {
                    e.preventDefault();
                    setPassword(e.target.value);
                }}/>
                <Button color='primary' onClick={(e) => {
                    e.preventDefault();
                    const body = {
                        username: username,
                        password: password
                    };
                    axios.post("/login", body)
                        .then(res => res.data)
                        .then(function (data) {
                            window.sessionStorage.setItem("sessionUsername", data.username);
                            window.sessionStorage.setItem("logged_in", data.logged_in);
                        })
                        .catch(err => alert(err))
                    if (window.sessionStorage.getItem("logged_in") === true){
                        <Navigate to='/'/>
                    }
                }}>Giriş Yap</Button> 
            </form>
        </div>
    </div>

  )
}

export default Login
