import axios from 'axios';
import React, { useState } from 'react'
import { Button, Input } from 'reactstrap';

const Register = () => {
    const [name, setName] = useState("");
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [confirm, setConfirm] = useState(""); 

    const handleRegister = (e) => {
        const registerBody = {
            name: name,
            username: username,
            email: email,
            password: password,
            confirm: confirm
        };

        axios.post("/register", registerBody)
            .then(res => console.log(res.status))
            .catch(err => console.log(err));
    }
  return (
    <div>
        <form>
            <Input label="Name" name='name' type='text' onChange={(e) => {
                e.preventDefault();
                setName(e.target.value);
            }}/>
            <Input label="Username" name='username' type='text' onChange={(e) => {
                e.preventDefault();
                setUsername(e.target.value);
            }}/>
            <Input label="Email" name='email' type='text' onChange={(e) => {
                e.preventDefault();
                setEmail(e.target.value);
            }}/>
            <Input label="Password" name='password' type='password' onChange={(e) => {
                e.preventDefault();
                setPassword(e.target.value);
            }}/>
            <Input label="Confirm" name='confirm' type='password' onChange={(e) => {
                e.preventDefault();
                setConfirm(e.target.value);
            }}/>
            <Button color='primary' onClick={handleRegister}>KayÄ±t Ol</Button>
        </form>
    </div>
  )
}

export default Register
