import axios from 'axios';
import React, { useState } from 'react'
import { Button, Input, Label } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const Register = () => {
    const [name, setName] = useState("");
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [confirm, setConfirm] = useState(""); 

    const handleRegister = (e) => {
        const registerBody = {
            name: name,
            username: username,
            email: email,
            password: password,
            confirm: confirm
        };

        axios.post("/register", registerBody)
            .catch(err => console.log(err));
    }
  return (
    <div>
        <form>
            <Label htmlFor='nm'>Name</Label>
            <Input id='nm' name='name' type='text' onChange={(e) => {
                e.preventDefault();
                setName(e.target.value);
            }}/> <br/>
            <Label htmlFor='uname'/>
            <Input id='uname' name='username' type='text' onChange={(e) => {
                e.preventDefault();
                setUsername(e.target.value);
            }}/> <br/>
            <Input name='email' type='text' onChange={(e) => {
                e.preventDefault();
                setEmail(e.target.value);
            }}/> <br/>
            <Input name='password' type='password' onChange={(e) => {
                e.preventDefault();
                setPassword(e.target.value);
            }}/> <br/>
            <Input name='confirm' type='password' onChange={(e) => {
                e.preventDefault();
                setConfirm(e.target.value);
            }}/> <br/>
            <Button color='primary' onClick={handleRegister}>KayÄ±t Ol</Button>
        </form>
    </div>
  )
}

export default Register
